from django.contrib.auth.forms import AuthenticationFormimport floppyforms as formsfrom crispy_forms.helper import FormHelperfrom crispy_forms.layout import Layout, Submit, Field, ButtonHolder, Divfrom .models import UserQueryclass ContactForm(forms.ModelForm):    def __init__(self, *args, **kwargs):        super(ContactForm, self).__init__(*args, **kwargs)        self.helper = FormHelper()        self.helper.form_action = 'contact_leapkit'        self.helper.layout = Layout(            Field('name'),            Field('email'),            Div(template="layout/quill.html"),            Field('body', type="hidden", id="hiddenBody"),            ButtonHolder(                Submit('submit', 'Contact', css_class="col-md-4 col-xs-4 col-xs-offset-4 col-md-offset-4")            )        )    class Meta:        model = UserQueryclass SignInForm(AuthenticationForm):    def __init__(self, *args, **kwargs):        super(SignInForm, self).__init__(*args, **kwargs)        self.helper = FormHelper()        self.helper.form_id = 'id-SignInForm'        self.helper.form_method = 'post'        self.helper.form_action = 'auth_view'        self.helper.add_input(Submit('submit', 'Submit'))